import { Component } from 'react';
import fetch from 'isomorphic-unfetch';
import FetchApi from './../helper/fetchApi';
import Layout from './../components/layout/layout';

const user_icon = './../static/name.png';

//import { login } from '../utils/auth'

class Index extends Component {
  static getInitialProps ({ req }) {
    return "Join twitter";
  }

  constructor (props) {
    super(props)

    this.state = { 
      email: '', 
      password:'', 
      error: '',
      loginApi: '/login'
    }

    this.handleChange = this.handleChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange (event) {
    this.setState({ email: event.target.value })
  }

  handlePasswordChange (event) {
    this.setState({ password: event.target.value })
  }

  handleTextChange(name, event) {
    this.setState({ [name]: event.target.value })
  }

  async handleSubmit (event) {
    event.preventDefault()
    const email = this.state.email;
    if (email.value === "") {
      window.alert("Please enter your email.");
      email.focus();
      return false;
    }
    const password = this.state.password;

    try {
      await FetchApi.fetchApiServerSide(this.state.loginApi, 'POST', {email:email, password:password})
      .then(response => {
        var data = response.data;
        var profile_page = (data)?data.profile_page:'/';
        Router.push(profile_page);
      })
    } catch (error) {
      console.error('You have an error in your code or there are Network issues.', error)
      //throw new Error(error)
    }
  }

  render () {
    return (
      <Layout>
        <div className="modal-content">
          <span className="close" onClick={this.closeModal}>&times;</span>
          <div className="right_side_form">
            <div className="text-center"><h1> Sign in</h1></div>
            <div className="text-center">Don't have an account ? <a onClick={this.openModalsignup}>Signup</a></div>
            <div className="form_div">
              <form className="text-center MT20">
                <div className="form-group">
                  <input type="text" name="field1" placeholder="Email" onChange={this.handleTextChange.bind(this, "email")} />
                  <img src={user_icon} alt="user" />
                </div>

                <div className="form-group">
                  <input id='signuin_pass' autocomplete="on" type="password" name="psw" placeholder="Password" onChange={this.handleTextChange.bind(this, "password")} />
                  {this.state.close_hidden ? <img src={eye_close} onClick={this.handlePassShow.bind(this, "signuin_pass")} alt="eye" /> : null}
                  {this.state.open_hidden ? <img src={eye_open} className="open_eye" onClick={this.handlePassShow.bind(this, "signuin_pass")} alt="eye" /> : null}

                </div>
                {/* <div className="text-left">
                  <input type="checkbox" name="agree" defaultChecked={this.state.signInRememberMe} onChange={this.handleCheckBox.bind(this)} /> Remember Me
                <a onClick={this.openModalforgotpassword} className="pull-right">Forgot Password?</a>
                  </div> */}

                  <div className="form-group">
                    <button className="brown-btn" type="button" onClick={this.handleSignIn.bind(this)}>SIGNIN</button>
                  </div>

                  <input type="checkbox" name="agree" defaultChecked={this.state.terms} onChange={this.handleTermCheckBox.bind(this)} /> By Continuing you agree to our <a href={FRONT_PANEL_DOMAIN + 'terms-and-conditions'}>Terms of Service</a> and <a href={FRONT_PANEL_DOMAIN + 'privacy-policy'}>Privacy Policy</a>

                  <div className="other_login">
                    <span className="text">or Login with </span>
                    <a href="#"><img src={fb_login} alt="facebook" /></a>
                    <a href="#"><img src={tw_icon} alt="twitter" /></a>
                    <a href="#"><img src={g_plus} alt="googleplus" /></a>
                  </div>
                </form>
              </div>
            </div>
          </div>

        <style jsx>{`
          .login {
            max-width: 340px;
            margin: 0 auto;
            padding: 1rem;
            border: 1px solid #ccc;
            border-radius: 4px;
          }
          form {
            display: flex;
            flex-flow: column;
          }
          label {
            font-weight: 600;
          }
          input {
            padding: 8px;
            margin: 0.3rem 0 1rem;
            border: 1px solid #ccc;
            border-radius: 4px;
          }
          .error {
            margin: 0.5rem 0 0;
            display: none;
            color: brown;
          }
          .error.show {
            display: block;
          }
        `}</style>
      </Layout>
    )
  }
}

export default Index
