import React, {Component} from 'react';
import Layout from './../components/layout/layout';

class Index extends Component{

    constructor(props){
        super(props);
        this.state = {
            name:null,
            email:null,
        }

        this.onChange = this.onChange.bind(this);
    }

    onChange(){
        console.log("On change function");
    }

    render(){
       return (
           <div>
                <Layout>
                <div className="AppContent wrapper wrapper-signup" id="page-container">
                    <link rel="stylesheet" href="https://abs.twimg.com/a/1511833274/css/t1/t1_signup.bundle.css"></link>
                    <div className="page-canvas">
                        <div className="signup-wrapper">
						<h1>Join Twitter Clone today.</h1>
                        </div>
                        {/* <form  className="t1-form signup" onSubmit={this.onSubmit}>
                            <label for="name">{this.state.name}</label>
                            <input type="text" name="name" value={this.state.name} onChange={this.onChange}/>
                            <button type="submit">Submit</button>
                        </form> */}
                        {/* <div className="t1-form signup " id="phx-signup-form">
							<div className="textbox">
								<div className="prompt name">
									<div data-fieldname="name" className="field">
										<div className="sidetip">
											<p id="nameerror" role="alert" className="blank invalid error">What's your name?</p>
										</div>
										<input type="text" placeholder="Full name" aria-required="true" id="full-name" className="">
                                        </input>
									</div>
								</div>
                                <div class="prompt name">
									<div data-fieldname="name" class="field">
										<div class="sidetip">
											<p id="uiderror" role="alert" class="blank invalid error">Write a user id</p>
										</div>
										<input type="text" placeholder="User Id" aria-required="true" id="uid" class=""></input>
									</div>
								</div>

                            </div>
                        </div> */}
                        <div className='login'>
          <form onSubmit={this.handleSubmit}>
            <label htmlFor='username'>GitHub username</label>

            <input
              type='text'
              id='username'
              name='username'
              value={this.state.username}
              onChange={this.handleChange}
            />

            <button type='submit'>Login</button>

            <p className={`error ${this.state.error && 'show'}`}>
              {this.state.error && `Error: ${this.state.error}`}
            </p>
          </form>
        </div>
        <style jsx>{`
          .login {
            max-width: 340px;
            margin: 0 auto;
            padding: 1rem;
            border: 1px solid #ccc;
            border-radius: 4px;
          }
          form {
            display: flex;
            flex-flow: column;
          }
          label {
            font-weight: 600;
          }
          input {
            padding: 8px;
            margin: 0.3rem 0 1rem;
            border: 1px solid #ccc;
            border-radius: 4px;
          }
          .error {
            margin: 0.5rem 0 0;
            display: none;
            color: brown;
          }
          .error.show {
            display: block;
          }
        `}</style>
                    </div>    
                </div>
                </Layout>
            </div>
        );
    }
}

export default Index;
