import { Component } from 'react';
import fetch from 'isomorphic-unfetch';
import FetchApi from './../helper/fetchApi';
import Layout from './../components/layout/layout';
import './../static/signup.css';
import { Paper, withStyles, Grid, TextField, Button, FormControlLabel, Checkbox } from '@material-ui/core';
import { Face, Fingerprint } from '@material-ui/icons'

const user_icon = './../static/name.png';
const FRONT_PANEL_DOMAIN = "http://localhost:3000/";
//import { login } from '../utils/auth'

const styles = theme => ({
  margin: {
      margin: theme.spacing.unit * 2,
  },
  padding: {
      padding: theme.spacing.unit
  }
});



class Index extends Component {
  static getInitialProps ({ req }) {
    return "Join twitter";
  }

  constructor (props) {
    super(props)

    this.state = { 
      email: '', 
      password:'', 
      error: '',
      loginApi: '/login'
    }

    this.handleChange = this.handleChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange (event) {
    this.setState({ email: event.target.value })
  }

  handlePasswordChange (event) {
    this.setState({ password: event.target.value })
  }

  handleTextChange(name, event) {
    this.setState({ [name]: event.target.value })
  }

  async handleSubmit (event) {
    event.preventDefault()
    const email = this.state.email;
    if (email.value === "") {
      window.alert("Please enter your email.");
      email.focus();
      return false;
    }
    const password = this.state.password;

    try {
      await FetchApi.fetchApiServerSide(this.state.loginApi, 'POST', {email:email, password:password})
      .then(response => {
        var data = response.data;
        var profile_page = (data)?data.profile_page:'/';
        Router.push(profile_page);
      })
    } catch (error) {
      console.error('You have an error in your code or there are Network issues.', error)
      //throw new Error(error)
    }
  }

  async handleSignIn(){

  }

  handleTermCheckBox = (e) => {
    // console.log('eventt=====', e, e.currentTarget.value)
    this.setState({ terms: !this.state.terms });
  };

  render () {
    const { classes } = this.props;

    return (
      <div>
        <Layout>
        <Paper className={classes.padding}>
                <div className={classes.margin}>
                    <Grid container spacing={8} alignItems="flex-end">
                        <Grid item>
                            <Face />
                        </Grid>
                        <Grid item md={true} sm={true} xs={true}>
                            <TextField id="username" label="Username" type="email" fullWidth autoFocus required />
                        </Grid>
                    </Grid>
                    <Grid container spacing={8} alignItems="flex-end">
                        <Grid item>
                            <Fingerprint />
                        </Grid>
                        <Grid item md={true} sm={true} xs={true}>
                            <TextField id="username" label="Password" type="password" fullWidth required />
                        </Grid>
                    </Grid>
                    <Grid container alignItems="center" justify="space-between">
                        <Grid item>
                            <FormControlLabel control={
                                <Checkbox
                                    color="primary"
                                />
                            } label="Remember me" />
                        </Grid>
                        <Grid item>
                            <Button disableFocusRipple disableRipple style={{ textTransform: "none" }} variant="text" color="primary">Forgot password ?</Button>
                        </Grid>
                    </Grid>
                    <Grid container justify="center" style={{ marginTop: '10px' }}>
                        <Button variant="outlined" color="primary" style={{ textTransform: "none" }}>Login</Button>
                    </Grid>
                </div>
            </Paper>
        </Layout>
      </div>
    )
  }
}

export default Index
