import { Component } from 'react';
import fetch from 'isomorphic-unfetch';

var HEADER_OBJECT = {'Accept': 'application/json', 'Content-Type': 'application/json' };
var HTTP_METHOD_GET = 'GET';
var HTTP_METHOD_POST = 'POST';
var FRONTEND_DOMAIN = 'http://localhost:4000';

class FetchApi extends Component {

    async fetchApiServerSide(req, res, apiUrl, method, body={}){
        var response = null;
        try{
            if(method === HTTP_METHOD_GET){
                response = await fetch(apiUrl, {
                        method: HTTP_METHOD_GET,
                        headers: HEADER_OBJECT
                    },5000)
                    .catch(rejected => {    
                        if(rejected === 'Timeout error'){
                            res.redirect(FRONTEND_DOMAIN + '/not-found/ApiTimeout');
                            res.end();
                        }else{
                            res.redirect(FRONTEND_DOMAIN + '/not-found/ApiDown');
                            res.end();
                        }
                    });
            }
            else{
                response = await fetch(apiUrl, {
                    method: HTTP_METHOD_POST,
                    headers: HEADER_OBJECT,
                    body: JSON.stringify(body),
                },5000).catch(rejected => {
                    if(rejected === 'Timeout error'){
                        res.redirect(FRONTEND_DOMAIN + '/not-found/ApiTimeout');
                        res.end();
                    }else{
                        res.redirect(FRONTEND_DOMAIN + '/not-found/ApiDown');
                        res.end();
                    }
                });
            }

            //Handle Api Response
            //reads the response stream to completion and parses the response as json.
            var apiResponse = await response.json();
            var responseCode = apiResponse.status;
            return apiResponse;
            // if(responseCode === 200){
            // }
            // else if(responseCode === 301){ 
            //     res.direct("/");
            //     res.end();
            // }
            // else if(responseCode === 404 || responseCode === 500 ){
            //     res.redirect('/not-found/'+apiResponse.status);
            //     res.end();
            // } 
            // else{
            //     res.redirect(FRONTEND_DOMAIN + '/not-found/ApiDown');
            //     res.end();
            // }    
        }catch(error){
            console.log("Try...catch error", error);
            return error;
        }
    }

    async fetchApiClientSide(){
        var response = null;
        try{
            if(method === HTTP_METHOD_GET){
                response = await fetch(apiUrl, {
                        method: HTTP_METHOD_GET,
                        headers: HEADER_OBJECT
                    },5000)
                    .catch(rejected => {    
                        if(rejected === 'Timeout error'){
                            window.location.href = FRONTEND_DOMAIN + '/not-found/ApiTimeout';
                        }else{
                            window.location.href = FRONTEND_DOMAIN + '/not-found/ApiDown';
                        }
                    });
            }
            else{
                response = await fetch(apiUrl, {
                    method: HTTP_METHOD_POST,
                    headers: HEADER_OBJECT,
                    body: JSON.stringify(body),
                },5000).catch(rejected => {
                    if(rejected === 'Timeout error'){
                        window.location.href = FRONTEND_DOMAIN + '/not-found/ApiTimeout';
                    }else{
                        window.location.href = FRONTEND_DOMAIN + '/not-found/ApiDown';
                    }
                });
            }

            //Handle Api Response
            //reads the response stream to completion and parses the response as json.
            var apiResponse = await response.json();
            console.log(apiResponse);
            var responseCode = apiResponse.status;
            if(responseCode === 200){
            }
            else if(responseCode === 301){ 
                window.location.href = "/";
            }
            else if(responseCode === 404 || responseCode === 500 ){
                window.location.href = '/not-found/'+apiResponse.status;
            } 
            else{
                window.location.href = FRONTEND_DOMAIN + '/not-found/ApiDown';
            }    
        }catch(error){
            console.log("Try...catch error", error);
            return error;
        }
    }
}

module.exports = new FetchApi();
