import React, {Component} from 'react';
import Layout from './../components/layout/layout';

class Index extends Component{

    constructor(props){
        super(props);
        this.state = {
            name:null,
            email:null,
        }

        this.onChange = this.onChange.bind(this);
    }

    onChange(){
        console.log("On change function");
    }

    render(){
       return (
           <div>
                <Layout>
                <div className="AppContent wrapper wrapper-signup" id="page-container">
                    <link rel="stylesheet" href="https://abs.twimg.com/a/1511833274/css/t1/t1_signup.bundle.css"></link>
                    <div className="page-canvas">
                        <div className="signup-wrapper">
						<h1>Join Twitter Clone today.</h1>
                        </div>
                        {/* <form  className="t1-form signup" onSubmit={this.onSubmit}>
                            <label for="name">{this.state.name}</label>
                            <input type="text" name="name" value={this.state.name} onChange={this.onChange}/>
                            <button type="submit">Submit</button>
                        </form> */}
                        <div className="t1-form signup " id="phx-signup-form">
							<div className="textbox">
								<div className="prompt name">
									<div data-fieldname="name" class="field">
										<div class="sidetip">
											<p id="nameerror" role="alert" class="blank invalid error">What's your name?</p>
										</div>
										<input type="text" placeholder="Full name" aria-required="true" maxlength="50" id="full-name" class="">
                                        </input>
									</div>
								</div>
                            </div>
                        </div>
                    </div>    
                </div>
                </Layout>
            </div>
        );
    }
}

export default Index;
