import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import $ from "jquery";
import fetchApi from './../helpers/fetchApi';
import {domain, ApiEndPoints, cdnPath, seoTrackerClass} from './../config';
import DocumentMeta from 'react-document-meta';
import BlogCard from './../containers/Cards/BlogCard';
import PosterCard from './../containers/Cards/PosterCard';
import HomePageCard from './../containers/Cards/HomeWidgetCards';
import HomeStreamerCards from './../containers/Cards/HomeStreamerCards';
import { settingsNewSm as sliderSmall, settingsNewSm2 as sliderMedium, settingsNewLg as sliderLarge ,settingsStream } from './../components/card/carouselSetting';
import { Tabs, Carousel } from 'antd';
import AutosuggestCard from './../containers/Cards/AutosuggestCard';
import fetch from 'isomorphic-unfetch';
import './../assets/css/common.css';
import './../assets/css/antd.css';
import Layout from './../layout/layout';
import SeoMetaTags from './../layout/SeoMetaTags';

const greySearchIcn = cdnPath+'/assets/image/d-search-grey.svg';
const TabPane = Tabs.TabPane;
const mainBanner = cdnPath+'/assets/image/main-banner.jpg';
const tvIcn = cdnPath+'/assets/image/tv-icn.svg';
const movieIcn  = cdnPath+'/assets/image/movie-icn.svg';

const HOMEPAGE_PROVIDERS_SCROLL_ARROW = seoTrackerClass.PROVIDERS_SCROLL_ARROW;
const HOMEPAGE_LANGUAGE_SCROLL_ARROW  = seoTrackerClass.LANGUAGES_SCROLL_ARROW;
const HOMEPAGE_LATEST_SCROLL_ARROW    = seoTrackerClass.LATEST_STORIES_SCROLL_ARROW;
const HOMEPAGE_GENRES_SCROLL_ARROW    = seoTrackerClass.GENRES_SCROLL_ARROW;
const LATEST_STORIES_VIEW_ALL_ID      = seoTrackerClass.LATEST_STORIES_VIEW_ALL_ID;
const PROVIDERS_VIEW_ALL_ID           = seoTrackerClass.PROVIDERS_VIEW_ALL_ID;
const LANGUAGES_VIEW_ALL_ID           = seoTrackerClass.LANGUAGE_VIEW_ALL_ID;
const GENRES_VIEW_ALL_ID              = seoTrackerClass.GENRES_VIEW_ALL_ID;

var trackerClasses = [];

function callback(key) {
    console.log(key);
}

class v3home extends Component {

    static async getInitialProps ({ req, res }) {      
        var homeResult = [];
        await fetchApi.read(ApiEndPoints.v3home,'get', {}, req, res)
         .then(res => {
                homeResult = (res.data) ? (res.data) :[];
          })
          .catch(err =>{
              console.log(err);
          })
        return {homeResult};
    }

    constructor(props){
        super(props)
        this.state = {
            homeResult: props.homeResult,
            headerAutosuggest: null,
        };

        this.getTrackerClass = this.getTrackerClass.bind(this);
        this.homeData = this.homeData.bind(this);
        this.getViewAllId = this.getViewAllId.bind(this);
    }

    componentDidMount(){
        /** Tracker Id For Home Widgets Scroll Arrow **/
        $('.streamers-block').find('.slick-arrow').attr('id', HOMEPAGE_PROVIDERS_SCROLL_ARROW);           
        $('.latest-stories-block').find('.slick-arrow').attr('id', HOMEPAGE_LATEST_SCROLL_ARROW);

        var trackerClassArray = trackerClasses;
        trackerClassArray.forEach(element => {
            /** Id on each tab of a widget for tracking events **/
            $('.new-tab-'+element).find('[role="tab"]').attr('id', element);           
            $('#'+element).find('.slick-arrow').attr('id', element);           
        });

        $('#lang-card-id').find('.slick-arrow').attr('id', HOMEPAGE_LANGUAGE_SCROLL_ARROW);           
        $('#genre-card-id').find('.slick-arrow').attr('id', HOMEPAGE_GENRES_SCROLL_ARROW);           

        $('div#homeSearchInput').click(function(){
            $('.openMobileSearch').click();
        });

        $(document).click(function(){
            $(".auto_sugg_ul").hide();
        });
    }

    autosuggest(e) {
        var searchText = e.target.value;
        if(searchText.length > 1){
            var finalListEndPoint = domain+'search/'+searchText;
            fetchApi.read(finalListEndPoint,'get')
                .then(res => this.setState({ 
                    searchResultset:res.data.results,
                }));      

             this.setState({headerAutosuggest: true});
        }else{
            this.setState({headerAutosuggest: false});
        }
    }

    componentWillMount(){
    }

    componentWillUnmount(){
    }

    // handleClick =(e)=>{
    //     if( e.target.className === 'auto_sugg_ul scrollable-area center' || e.target.className === 'home-search-input' || e.target.className === 'as-list' || e.target.className === 'as-info1' || e.target.className === 'as-info2'){
    //         this.setState({headerAutosuggest: true});
    //         return;
    //     }
    //     if(this.node.contains(e.target)){
    //         return;
    //     }
    //     this.handleClickOutside();
    // }

    // handleClickOutside =(e)=>{
    //     this.setState({headerAutosuggest: false});
    // }

    handleKeyUp = (e) => {
        var storeTarget = '';
        if(e.keyCode === 13){
            var searchText =  e.target.value;
            if(searchText !== null && searchText !== '' && searchText !== undefined){
                window.location = '/search-result/'+searchText;
            }
        }
        if (e.keyCode === 40) {    
            if($("ul.auto_sugg_ul li.autoSelected").length !== 0) {           
                storeTarget	= $('ul.auto_sugg_ul').find("li.autoSelected").next();
                $("ul.auto_sugg_ul li.autoSelected").removeClass("autoSelected");
                storeTarget.focus().addClass("autoSelected");
            }else {        
                $('ul.auto_sugg_ul').find("li:first").focus().addClass("autoSelected");
            }
        }

        if (e.keyCode === 38) {    
            if($("ul.auto_sugg_ul li.autoSelected").length !== 0) {
                storeTarget	= $('ul.auto_sugg_ul').find("li.autoSelected").prev();
                $("ul.auto_sugg_ul li.autoSelected").removeClass("autoSelected");
                storeTarget.focus().addClass("autoSelected");
            }else {
                    $('ul.auto_sugg_ul').find("li:first").focus().addClass("autoSelected");
            }
        }
    }

    AutosuggestWidgetDiv() {
        const result = this.state.searchResultset;
        if (result instanceof Array) {
            return result.map(function (object, index) {
                return <AutosuggestCard object={object} className=""></AutosuggestCard>
            })
        }
    }

    getTrackerClass(singleTab){
        var widgetName = (singleTab.widgetName)?singleTab.widgetName:null;
        var content = (singleTab.widgetContent)?singleTab.widgetContent:null;
        content = (content !== null)?((content === 'movie')?'movies':'tv_shows'):null;
        var trackerClass = (widgetName == null)?'streamers':(content == null)?widgetName:widgetName+"_"+content;
        switch(widgetName) {
            case 'LATEST STORIES':
                return 'latest_stories';
            case 'GENRES':
                return 'genres';
            case 'LANGUAGES':
                return 'languages'; 
            case 'latest':
                return 'new_'+content;                                                 
            default:
              return trackerClass;
        }
    }

    homeData(){
        const homeApiResult = this.props.homeResult;
        let widgets = homeApiResult.map((widget) => {
            var card_id = '';
            var cardData = '';
            var widgetTabBlock = '';   
            var viewAllId = '';         
            var sliderSetting = sliderLarge;
            var classCrousal = "eq-height card-slider m-lg-card slide-1";

            /** PROVIDERS WIDGET STARTS **/
            if(widget.card === 'HomeStreamerCards'){
                classCrousal  = "eq-height card-slider m-sm2-card slide-1 streamers-block";
                sliderSetting = settingsStream;
                viewAllId     = PROVIDERS_VIEW_ALL_ID;
                cardData = widget.data.map((single) => {
                    var singleData = <HomeStreamerCards Cardobject={single} key={single.seoUrl.toString()}></HomeStreamerCards>;
                    return singleData;
                });
                widgetTabBlock = <Carousel className={classCrousal} {...sliderSetting}>{cardData}</Carousel>
            }
            /** PROVIDERS WIDGET ENDS **/

            /** LATEST STORIES STARTS **/
            if(widget.card === 'BlogCard'){
                classCrousal = "eq-height card-slider m-lg-card slide-1 latest-stories-block";
                viewAllId    = LATEST_STORIES_VIEW_ALL_ID;
                card_id      = 'slide-blog';
                cardData = widget.data.map((single) => {
                    var singleData = <BlogCard key={single.url.toString()} Cardobject={single}></BlogCard>;
                    return singleData;
                });
                widgetTabBlock = <Carousel className={classCrousal} {...sliderSetting}>{cardData}</Carousel>
            }
            /** LATEST STORIES ENDS **/

            /** CONTENT WIDGETS STARTS **/
            if(widget.card === 'PosterCard'){
                sliderSetting = sliderMedium; 
                var widgetName = widget.widgetName;
                var selectedTab = widget.data[0];
                var allTab = widget['data'];  
                var trackerClass = this.getTrackerClass(widget);
                card_id = trackerClass;
                var trackerClassArray = trackerClasses;
                trackerClassArray.push(trackerClass);
                trackerClasses = trackerClassArray;

                var eachTabBlock = allTab.map((singleTab) => { 
                    var arrayElements = [];
                    var dataDiv = '';
                    var viewAllCard = '';
                    var viewAllId = this.getViewAllId(widgetName, widget.widgetContent, singleTab.tabTitle);
                    sliderSetting = sliderMedium;
                    var cardData = singleTab.data.map((single) => {
                        var singleData = <PosterCard widgetName={widgetName} widgetId={trackerClass} key={single.contentId.toString()} Cardobject={single}></PosterCard>
                        return singleData;
                    });

                    dataDiv = cardData;
                    viewAllCard = <div className="card-div lg-card mr-3 viewall-card">
                                    <a href={singleTab.selectedUrl} id={viewAllId}>
                                    <div className="viewall-icn">&#8594;</div>
                                    <div className="viewall-text">{singleTab.selectedTitle}</div>
                                    </a>
                                </div>;

                    var tabPane = <TabPane tab={singleTab.tabTitle} key={singleTab.tabTitle} forceRender={true} >
                                    <div name={"1"} >
                                        <Carousel className={classCrousal} {...sliderSetting}>{dataDiv}{viewAllCard}</Carousel>
                                    </div>
                                    </TabPane>
                    arrayElements.push(tabPane);
                    return arrayElements;
                });
                widgetTabBlock = <Tabs defaultActiveKey={selectedTab.tabTitle} 
                onChange={callback} 
                className={`custom-tab new-tab-`+trackerClass} 
                >{eachTabBlock}</Tabs>;
            }
            /** CONTENT WIDGETS ENDS **/


            /** LANGUAGE & GENRES WIDGET STARTS **/
            if(widget.card === 'HomePageCard'){
                sliderSetting = sliderSmall;
                classCrousal = "eq-height card-slider m-sm-card slide-1";
                if(widget.title ==='LANGUAGES'){
                    viewAllId = LANGUAGES_VIEW_ALL_ID;
                    card_id = "lang-card-id";
                    cardData = widget.data.map((single) => {
                        var singleData = <HomePageCard key={single.seoUrl.toString()} type="Language" Cardobject={single}></HomePageCard>;
                        return singleData;
                    });
                }else{
                    viewAllId = GENRES_VIEW_ALL_ID;
                    card_id = 'genre-card-id';
                    cardData = widget.data.map((single) => {
                        var singleData = <HomePageCard key={single.seoUrl.toString()} Cardobject={single}></HomePageCard>;
                        return singleData;
                    });
                }
                widgetTabBlock = <Carousel className={classCrousal} {...sliderSetting}>{cardData}</Carousel>
            }
            /** LANGUAGE & GENRES WIDGET ENDS **/


            var widgetBlock = <div key={widget.widgetTitle.toString()} className="no-m-card-component card-component padd pos-relative" >
                                <div className="card-wrapper no-overflow streamwrapper" id={card_id}>
                                    <div className="card-header mt-2">
                                        {widget.widgetTitle}{widget.viewUrl ? <a href={widget.viewUrl} target="_blank" className="card-view-all mr-2" id={viewAllId}>{widget.viewTitle}</a>:''}
                                    </div>
                                    {widgetTabBlock}
                                </div>
                              </div>

            return widgetBlock;
        });

        return widgets
    }

    getViewAllId(widgetName, widgetContent, tabTitle){
        var name_lowerCase = widgetName.toLowerCase();
        var content = (widgetContent === 'movie')?'movies':'tv-shows';
            tabTitle = tabTitle.toLowerCase();
        var viewAllId = 'view-'+tabTitle+"-"+name_lowerCase+'-'+content;
        return viewAllId;
    }

    render(){
        var meta = {
            title: 'MetaReel - Stream Movies and TV Series / Shows Online',
            description: 'Find and stream popular and latest Movies and TV Shows / Series Online on Indiaâ€™s leading Streaming Search Engine MetaReel.com.',
            meta: {
              charset: 'utf-8',
              name: { 
                  keywords: 'MetaReel - Stream Movies and TV Series Online', 
                  robots: 'index,follow'
                },
            }
        };

        var headerAutosuggest = '';
        if(this.state.headerAutosuggest === true){
            headerAutosuggest = <div className="center-search-screen">
                                    <ul className="auto_sugg_ul scrollable-area center">
                                        <div className="search-title">
                                        </div>
                                        {this.AutosuggestWidgetDiv()}
                                    </ul>
                                </div>
        }

        return (
            <div id="root" className="page-wrap">
            <SeoMetaTags metaData={meta}/>
            <Layout>
            <div className="metareel-home-page">
                <div className="home-banner">
                    <img className="w-100" src={mainBanner} alt="home-icon"/>
                    <div className="home-search">
                        <div className="home-search-title">THE STREAMING SEARCH ENGINE</div>
                        <div className="d-mobile-block home-search-input" id="homeSearchInput">Search for movies, tv shows or celebrities </div>
                        <input onKeyUp={this.handleKeyUp} className="d-desktop-block home-search-input" onChange={(e) => this.autosuggest(e)} placeholder="Search for movies, tv shows or celebrities" />
                        <i className="banner-serach-icn">
                            <img src={greySearchIcn} alt="search-banner"/>
                        </i>
                            { headerAutosuggest }
                    </div>
                </div>
                <div className="container homepage" ref={node =>this.node = node }>
                    <div className="home-tab m-d-flex">
                        <div className="tabblock"> 
                            <a href="/list/movies" className="m-list-movies">
                                <i className="tabicn">
                                    <img src={movieIcn} alt="movie-icon"/>
                                </i>
                                All Movies
                            </a>
                        </div>
                        <div className="tabblock">
                            <a href="/list/tv-shows" className="m-list-tv-shows">
                                <i className="tabicn">
                                    <img src={tvIcn} alt="tv-show-icon"/>
                                </i>
                            ALL TV SHOWS
                            </a>
                        </div>
                    </div> 
                    {this.homeData()}   
                </div>
            </div>
            </Layout>
            </div>
        );
    }

}
export default (v3home);
